using System.Linq;
using KioskBrains.Kiosk.Core.Modals;

namespace KioskApp.Ek.Info
{
    public static class InfoModalHelper
    {
        static InfoModalHelper()
        {
            _userAgreementBlocks = ParseInfoText(_userAgreementText);
            _warrantyBlocks = ParseInfoText(_warrantyText);
            _returnBlocks = ParseInfoText(_returnText);
        }

        public static void OpenInfoModal(InfoModalTypeEnum infoModalType)
        {
            EkContext.Current.EkProcess?.OnViewChanged($"Modal {infoModalType}", false);

#pragma warning disable 4014
            ModalManager.Current.ShowModalAsync(new ModalArgs(
#pragma warning restore 4014
                infoModalType.ToString(),
                context =>
                    {
                        var infoModal = new InfoModal(context);
                        switch (infoModalType)
                        {
                            case InfoModalTypeEnum.Warranty:
                                infoModal.TitlePart1 = "Условия";
                                infoModal.TitlePart2 = "гарантии";
                                infoModal.InfoBlocks = _warrantyBlocks;
                                break;
                            case InfoModalTypeEnum.Return:
                                infoModal.TitlePart1 = "Условия";
                                infoModal.TitlePart2 = "возврата";
                                infoModal.InfoBlocks = _returnBlocks;
                                break;
                            case InfoModalTypeEnum.UserAgreement:
                                infoModal.TitlePart1 = "Пользовательское соглашение";
                                infoModal.InfoBlocks = _userAgreementBlocks;
                                break;
                        }

                        return infoModal;
                    },
                showCancelButton: false));
        }

        private static readonly InfoBlock[] _userAgreementBlocks;

        private static readonly InfoBlock[] _warrantyBlocks;

        private static readonly InfoBlock[] _returnBlocks;

        private const string ListItemStart = "- ";

        public static InfoBlock[] ParseInfoText(string infoText)
        {
            if (string.IsNullOrEmpty(infoText))
            {
                return new InfoBlock[0];
            }

            var lines = infoText
                .Replace("\r", "")
                .Split("\n");

            return lines
                .Select(line =>
                    {
                        InfoBlockTypeEnum type;
                        string text;
                        if (string.IsNullOrWhiteSpace(line))
                        {
                            type = InfoBlockTypeEnum.ExtraSpace;
                            text = null;
                        }
                        else if (line.StartsWith(ListItemStart))
                        {
                            type = InfoBlockTypeEnum.ListItem;
                            text = line.Substring(ListItemStart.Length);
                        }
                        else
                        {
                            type = InfoBlockTypeEnum.Paragraph;
                            text = line;
                        }

                        return new InfoBlock()
                            {
                                Type = type,
                                Text = text,
                            };
                    })
                .ToArray();
        }

